#include <vector>
#include <string>
#include <map>
#include <iostream>
#include <algorithm>
#include <set>
#include <algorithm>

std::vector<std::vector<int>> threeSum_ugly(std::vector<int>& nums)
{
    std::map<int, std::set<int>> indexes;
    std::vector<int> numbers;
    std::set<std::vector<int>> used;
    std::vector<std::vector<int>> res;
    for (int i = 0; i < (int)nums.size(); ++i)
    {
        if (indexes.find(nums[i]) == indexes.end())
        {
            indexes[nums[i]] = {(int)numbers.size()};
            numbers.push_back(nums[i]);
        }
        else if (indexes[nums[i]].size() < 3)
        {
            indexes[nums[i]].insert(numbers.size());
            numbers.push_back(nums[i]);
        }
    }
    for (int i = 0; i < ((int)numbers.size() - 2); ++i)
    {
        for (int j = i + 1; j < ((int)numbers.size() - 1); ++j)
        {
            int missing = - (numbers[i] + numbers[j]);
            if (indexes.find(missing) != indexes.end()) 
            {
                auto& missing_indexes = indexes[missing];
                if (std::any_of(missing_indexes.begin(), missing_indexes.end(), [&, i, j](int x) {return x != j && x != i;}))
                {
                    std::vector<int> tmp({numbers[i], numbers[j], missing});
                    std::sort(tmp.begin(), tmp.end());
                    if (used.find(tmp) == used.end())
                    {
                        res.push_back(tmp);
                        used.insert(tmp);
                    }
                }
            }
        }
    }
    return res;
}

using namespace std;
vector<vector<int>> threeSum_good(vector<int> &nums) {
        vector<vector<int>> ans;
        const int target = 0;

        // Make nums in increasing order. Time: O(nlogn)
        sort(nums.begin(), nums.end());

        for (int i = 0; i < static_cast<int>(nums.size()) - 2; ++i) {
            if (i == 0 || nums[i] != nums[i - 1]) {  // Skip duplicated.
                for (int j = i + 1, k = nums.size() - 1; j < k; ) {  // Time: O(n) for each i.
                    if (j - 1 > i  && nums[j] == nums[j - 1]) {  // Skip duplicated.
                        ++j;
                    } else if (k + 1 < nums.size() && nums[k] == nums[k + 1]) {  // Skip duplicated.
                        --k;
                    } else {
                        const auto sum = nums[i] + nums[j] + nums[k];
                        if (sum > target) {  // Should decrease sum.
                            --k;
                        } else if (sum < target) {  // Should increase sum.
                            ++j;
                        } else {
                            ans.push_back({nums[i], nums[j], nums[k]});
                            ++j, --k;
                        }
                    }
                }
            }
        }

        return ans;
    }

